'kso/Store':function(){
/**
    Special module that handle data sync seemlessly with the kso
    backend module.

    Lazy load data to make sure we wait for data sync until user is ready.

    When updating the data in the store this will be synced with the backend
    if online, and if not the timer will retry after a delay.

    We may also do this by making out own store from scrash, but we settle for
    a modified memory store for now.
*/

define([
    'dojo/_base/declare',
    'dojo/topic',
    'dojo/_base/config',
    'dojo/store/Memory',
    'dojo/store/Observable',
    'dojo/store/JsonRest',
    'dojo/_base/Deferred',
    'dojo/store/util/QueryResults'
],
function( declare, topic, config, Memory, Observable, JsonRest, Deferred, QueryResults ) {
    var ms_delay = 1000 * 60 * 10;

    var topic_create = 'moch/entity_create';
    var topic_update = 'moch/entity_update';
    var topic_delete = 'moch/entity_delete';

    var service_uri = '/service';

    if( typeof config.service_uri == 'string' )
        service_uri = config.service_uri;

    return declare( null, {
        entity_name: null,

        constructor: function( options ) {
            for(var i in options)
                this[i] = options[i];

            this._store = Observable( new Memory() );
            this._rest_store = new JsonRest({target: service_uri + '/entity/' + this.entity_name + '/'});
            this._need_sync = true;
            this._updates = [];
            this._removes = [];
            this._inserts = [];
            this._thndl;

            var self = this;

            topic.subscribe( topic_update, function(entity, key, values) {
                if( self.entity_name == entity ) {
                    values.id = key;
                    self._store.put( values );
                    console.log( "topic update ", self.entity_name, values );
                }
            });

            topic.subscribe( topic_create, function( entity, values ) {
                if( self.entity_name == entity ) {
                    try {
                        if( ! self._store.get( values.id )) {
                            self._store.add( values );
                            console.log( "topic create ", self.entity_name, values );
                        }
                    } catch( e ) {
                        console.log( 'error adding new item to ', entity, ' store : ', e );
                    }
                }
            });

            topic.subscribe( topic_delete, function( entity, key ) {
                if(self.entity_name == entity) {
                    if( self._store.get( key )) {
                        self._store.remove( key );
                        console.log( "topic delete ", self.entity_name, key );
                    }
                }
            });
        },

        get: function( id ) {
            return this._store.get( id );
        },

        getIdentity: function(object) {
            return this._store.getIdentity(object);
        },

        put: function(object, options) {
            this._updates.push(object);
            this._start_update_data();
            //return this._store.put(object, options);
        },

        add: function(object, options) {
            this._inserts.push(object);
            this._start_update_data();
            //return this._store.add(object, options);
        },

        remove: function(id) {
            this._removes.push(id);
            this._start_update_data();
            //return this._store.remove(id);
        },

        query: function(query, options) {
            console.log( "query ", this.entity_name, query, options );

            if( this._need_sync ) {
                var rest_res = this._fetch_data(query, options);

                return rest_res;
            }
            return this._store.query(query, options);
        },

        _fetch_data: function( query, options) {
            if( this._need_sync ) {
                var d = new Deferred();

                var self = this;

                var rest_res = this._rest_store.query(query, options);

                var results = QueryResults( d );

                rest_res.then(
                    function( dlist ) {
                        self._store.setData( dlist );
                        self._need_sync = false;

                        var res = self._store.query(query, options);
                        results.observe = res.observe;
                        d.resolve( res );
                    },
                    function( err ) {
                        setTimeout( self._fetch_data, ms_delay );
                    }
                );
                return results;
            }
        },

        _start_update_data: function() {
            if( this._thndl )
                clearTimeout( this._thndl );

            this._update_data();
        },

        _update_data: function() {
            if( this._updates.length == 0 && this._inserts.length == 0 && this._removes.length == 0 ) {
                this._thndl = undefined;
                return;
            }

            var self = this;

            this._updates.forEach( function( item ) {
                self._rest_store.put( item ).then(
                    function( res ) {
                        // self._store.put( item );
                    },
                    function( err ) {
                        console.log( "error updating data", err );
                        this._updates.push( item );
                    }
                );
            });
            this._updates = [];

            this._inserts.forEach( function( item ) {
                var bogus_id = item.id;
                delete item.id;

                self._rest_store.add( item ).then(
                    function( res ) {
                        // self._store.add( res );
                    },
                    function( err ) {
                        console.log( "error adding data", err );
                        self._inserts.push( item );
                    }
                );
            });
            self._inserts = [];

            this._removes.forEach( function( item ) {
                self._rest_store.remove( item ).then(
                    function( res ) {
                        // self._store.remove( item.id );
                    },
                    function( err ) {
                        console.log( "error deleting data", err );
                        self._removes.push( item );
                    }
                );
            });
            this._removes = [];

            this._thndl = setTimeout( function() {self._update_data.apply(self);}, ms_delay );
        }
    });
});

},
